    <form name="lzform" method="post" action="/space/member/lostpwd" target="lostpwdiframe">
        <div class="item extra-tips">
            <label>电子邮箱</label>
            <input onBlur="checkemail()" name="email" id="email" type="text" size="24" tabindex="1" class="basic-input" maxlength="60" value="">
            <span class="validate-error" style="display: none;" id="tip_email"></span>
        </div>
        <div class="item">
            <label>验证码</label>

<!--<img id="captcha_image" src="./重设密码_files/captcha" alt="captcha" class="captcha_image">-->
            <p><span id="system_capcode"></span><span class="tips">看不清？<a href="javascript:;void();" onClick="changecapcode()" id="cap_resend">换一个</a></span></p>
<div class="captcha_block">
            <span id="captcha_block" class="pl">请输入上图中的数字</span>
            <input onblur="checkcapcode()" type="text" name="capcode" class="basic-input captcha" id="captcha_field" tabindex="3"  onblur="checkcapcode()">
            <span class="validate-error" style="display: none;" id="tip_capcode"></span>
  <!--<span id="captcha_block" class="pl">请输入上图中的单词</span>
  <input type="text" id="captcha_field" name="captcha-solution" tabindex="3" placeholder="验证码">
